import { createGlobalStyle} from "styled-components";

const GlobalStyle = createGlobalStyle`
header {
      float: left;
      justify-content: flex-end;
}



    body{
    margin: 0;
    padding: 0;
    /* width: 100vw;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center; */
    background-color: #2f0059;

    }


    button {
        
    margin-bottom: 15px;
    height: 30px;
    padding: 0 20px;
    border-radius: 20px;
    border: none;
    color: white;
    font-size: 16px;
    background-color: #28013c;
    min-width: 100px;

    &:hover{
      cursor: pointer;
      background-color: #b6d4e3;
    }


    }

    h1, h2, b {
    color: slategray;
  }

  h1 {
    text-align: center;
    font-family: Open-Sans, Helvetica, Sans-Serif;
  }

  h2 {
    text-align: center;

    font-family: Open-Sans, Helvetica, Sans-Serif;
  }

  p {
    color: #394145;
    font-family: Open-Sans, Helvetica, Sans-Serif;
  }

  select {
    width: 518px;
    height: 40px;
    border-radius: 10px;
    padding: 4px 8px;
    border-width: 1px;
    margin: 0 0 15px 0;
  }

  form {
    display: flex;
    flex-direction: column;
    align-items: center;
}
input {
    width: 300px;
    height: 30px;
    border-radius: 10px;
    border: 0.5px solid black;
    margin: 10px

}    

/* select {
    width: 500px;
    height: 40px;
    border-radius: 10px;
    margin: 10px;

} */
}
`
export default GlobalStyle;
