import {useHistory} from "react-router-dom";
import {useState, useEffect} from "react";
import { TripsCard } from "../components/TripsCard";
// import {BASE_URL} from "/../constant/url"
import axios from "axios";
import styled from "styled-components";
import Header from "../components/Header";



const CardTrip = styled.div`
display: flex;

`

const ListTripsPage = () => {

    const [trips, setTrips] = useState ([])
    const [cards, setCards] = useState([])

    const getTrips = () => {
        axios
        .get('https://us-central1-labenu-apis.cloudfunctions.net/labeX/evelyn-oliveira-carver/trips')
        .then(res => {
            setTrips(res.data.trips)
            console.log(res.data.trips)
            
            
        })
        .catch(err => {
            console.log(err, "erro lista")
        }, [])
    }

    

    const showList = trips.map((item) => {
        return (
            <div>
            <p>{item.id}</p>
            <p>{item.nome}</p>
            </div>
        )
    })
        // return (
        //     <div>
                
        //     </div>
            
            // <CardTrip key={item.id}>
            
            // <h1>{item.name}</h1>
            // <p>
            // {item.description}
            // {item.planet}
            // {item.durationInDays}
            // {item.date}
            // </p>   
            // </CardTrip>  

        
    

    useEffect(()=> {
        getTrips();
    },[]);


    const history = useHistory()

    const goBackHome = () => {
        history.goBack()
    }

    const applicationForm = () => {
        history.push("/trips-application")
    }

    console.log(trips)

    return (
        <div>
            <Header/>
            {showList}

        <h2>Lista de Viagens</h2>
        


        <button onClick={goBackHome}>Voltar</button>
        <button onClick={applicationForm}>Increva-se</button>
        </div>
    )


}

export default ListTripsPage;
