import {useHistory} from "react-router-dom";
import {useState, useEffect} from "react";
import { TripsCard } from "../components/TripsCard";
// import {BASE_URL} from "/../constant/url"
import axios from "axios";
import styled from "styled-components";
import Header from "../components/Header";



const CardContainer = styled.div`
display: flex;

* {
  box-sizing: border-box;
}

body {
  font-family: Arial, Helvetica, sans-serif;
}

/* Float four columns side by side */
.column {
  float: left;
  width: 25%;
  padding: 0 10px;
}

/* Remove extra left and right margins, due to padding in columns */
.row {margin: 0 -5px;}

/* Clear floats after the columns */
.row:after {
  content: "";
  display: table;
  clear: both;
}

/* Style the counter cards */
.card {
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2); /* this adds the "card" effect */
  padding: 16px;
  text-align: center;
  background-color: #f1f1f1;
}

/* Responsive columns - one column layout (vertical) on small screens */
@media screen and (max-width: 600px) {
  .column {
    width: 100%;
    display: block;
    margin-bottom: 20px;
  }
}

`

const ListTripsPage = () => {

    const [trips, setTrips] = useState ([])
    const [cards, setCards] = useState([])

    const getTrips = () => {
        axios
        .get('https://us-central1-labenu-apis.cloudfunctions.net/labeX/evelyn-oliveira-carver/trips')
        .then(res => {
            setTrips(res.data.trips)
            console.log(res.data.trips)
            
            
        })
        .catch(err => {
            console.log(err, "erro lista")
        }, [])
    }

    

    const showList = trips.map((item) => {
        return (
            <TripsCard>
            <h2>{item.name}</h2>
            <p> <b>Planeta: </b>{item.planet}</p>
            <p> <b>Descrição: </b>{item.description}</p>
            <p> <b>Duração em dias: </b >{item.durationInDays}</p>
            <p> <b>Data: </b>{item.date}</p>
            </TripsCard>
        )
    })
       
    

    useEffect(()=> {
        getTrips();
    },[]);


    const history = useHistory()

    const goBackHome = () => {
        history.goBack()
    }

    const applicationForm = () => {
        history.push("/trips-application")
    }

    console.log(trips)

    return (
        
        <div>
            <Header/>
            

        <h2>Lista de Viagens</h2>
        <CardContainer>
        {showList}
        </CardContainer>
        
        


       
        
        
        <button onClick={goBackHome}>Voltar</button>
        <button onClick={applicationForm}>Increva-se</button>


        </div>



    )


}

export default ListTripsPage;
