import styled from "styled-components";
import { createGlobalStyle } from "styled-components";

export const Container = styled.div`
text-align: center;


`

export const HomeBox = styled.div`
margin-top:20%;

h1 {
    color: slategray;
}


}
`

export const LoginBox = styled.div`
display: flex;
flex-direction: column;
align-items: center;

button {
    color: white;
    background-color: slategray;
    border-radius: 20px;
    border: none;
    width: 10vw;
    height: 40px;
    font-size: 15px;
    margin-top: 1,5%;
    &:hover{
        background-color: gray;
    }
}
form {
    display: flex;
    flex-direction: column;
    align-items: center;
}
input {
    width: 500px;
    height: 40px;
    border-radius: 10px;
    border: 0.5px solid black;
    margin: 10px

}    

select {
    width: 500px;
    height: 40px;
    border-radius: 10px;
    margin: 10px;

}

`
