import styled from 'styled-components'

export const SplashContainer = styled.div `
    background: linear-gradient( 180deg #058EFF, #29C6FF);
    /* background-color:#FFFFFF; */
    width: 90vw;
    height: 90vh;
    display: flex;
    justify-content: center;
    align-items: center;
`

export const Logo = styled.img `
    width: 500px;
    height: 500px;
    object-fit: contain;
    `
