import React, { useEffect } from "react";
import { EditAddress } from '../../Services/Access'
import { useHistory } from "react-router-dom";
import useForm from '../../Hooks/useForm'
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios'
import { BASE_URL } from "../../Constants/URL";
import { useProtectedPage } from "../../Hooks/useProtectedPage";

const EditAddressForm = () => {
    useProtectedPage()
    const {form, handleInputOnChange, clear, setForm} = useForm({street:"", number:"", neighbourhood:"", city:"", state:"", complement:""})
    const history = useHistory()

    const onSubmitForm = (event) => {
        event.preventDefault()
        EditAddress(form, history)
    }

    const getAddress = () => {
        axios.get(`${BASE_URL}/profile/address`, {
          headers: {
            auth: localStorage.getItem("token")
          }
        })
          .then((response) => {
            setForm({ street: response.data.address.street, number: response.data.address.number, neighbourhood: response.data.address.neighbourhood, city: response.data.address.city, state: response.data.address.state, complement: response.data.address.complement})
          })
          .catch((err) => {
            alert(err.response.data.message)
          })
         
      }
    
      useEffect(()=>{
        getAddress()
      },[])

    return (
            <div>
                <form onSubmit={onSubmitForm}>
                    <TextField
                        name={"street"}
                        value={form.street}
                        onChange={handleInputOnChange}
                        label={"Rua/Av."}
                        type={"text"}
                        variant={"outlined"}
                        fullWidth
                        margin={"dense"}
                        required
                    />
                    <TextField
                        name={"number"}
                        value={form.number}
                        onChange={handleInputOnChange}
                        label={"NÃºmero"}
                        type={"number"}
                        variant={"outlined"}
                        fullWidth
                        margin={"dense"}
                        required
                    />
                    <TextField
                        name={"complement"}
                        value={form.complement}
                        onChange={handleInputOnChange}
                        label={"Apartamento/Bloco"}
                        type={"text"}
                        variant={"outlined"}
                        fullWidth
                        margin={"dense"}
                    />
                    <TextField
                        name={"neighbourhood"}
                        value={form.neighbourhood}
                        onChange={handleInputOnChange}
                        label={"Bairro"}
                        type={"text"}
                        variant={"outlined"}
                        fullWidth
                        margin={"dense"}
                        required
                    />
                    <TextField
                        name={"city"}
                        value={form.city}
                        onChange={handleInputOnChange}
                        label={"Cidade"}
                        type={"text"}
                        variant={"outlined"}
                        fullWidth
                        margin={"dense"}
                        required
                    />
                    <TextField
                        name={"state"}
                        value={form.state}
                        onChange={handleInputOnChange}
                        label={"Estado"}
                        type={"Text"}
                        variant={"outlined"}
                        fullWidth
                        margin={"dense"}
                        required
                    />
                    <Button 
                    fullWidth 
                    variant="contained"
                    type="submit"
                    >
                    <p>Salvar</p>
                    </Button>
                </form>
            </div>
    )
}

export default EditAddressForm;
