import React, { useContext, useEffect } from "react";
import PokemonCard from "../../Components/PokemonCard/PokemonCard";
import GlobalContext from "../../Contexts/GlobalContext";
import {goToDetails, goToPokedex} from "../../Routes/Coordinators"
import { useHistory } from 'react-router-dom';
import {HomeList} from './Style'

function HomePage() { 
    const {states, setters, requests} = useContext(GlobalContext) 
    const history = useHistory(); 
    const goToDetalhes = (name)=>{ 
        history.push(`/detail/${name}`) } 
        
    
    useEffect(()=>{ requests.getPokemons() }, []) 
    
    const pokeList = states.pokemons.results && states.pokemons.results.map((pokemon)=>{ 
        return( <PokemonCard key={pokemon.url} 
            name={pokemon.name} 
            order={pokemon.order} 
            url={pokemon.url} 
            add={()=> requests.addPokemon(pokemon)} 
            det={()=> goToDetalhes(pokemon.name)} /> ) }) 
            
            return ( 
            <HomeList> {pokeList === [] ? <p>Loading</p> : pokeList} 
            {/* {pokeList} */} <button onClick={()=> goToPokedex(history)}>Ir para pokedex</button> </HomeList> );}







export default HomePag
