import styled from "styled-components"

export const ContainerMain = styled.div`
display:flex;
flex-direction: grid;
justify-content: space-evenly;
margin: 50px 0px 0px;

p{
  font-size: 12px;
}


`



export const ContainerImg = styled.div`
display: flex;
background-color: #E4E6CC; 
border-radius: 20px;
width: 300px;
height: 300px;

`
export const ContainerTypes = styled.div`
background-color: #E4E6CC;
width: 300px;
height: 300px;
border-radius: 20px;


`
export const ContainerStats = styled.div`
background-color: #E4E6CC
width: 300px;
height: 300px;


`

export const ContainerMoves= styled.div`
background-color: #E4E6CC;
border-radius: 10px;
width: 300px;
height: 300px;
width: 200px;
`
