import React, { useContext } from "react"
import { goToHome, goToPokedex, goToDetails } from '../../Routes/Coordinator'
import { MainContainer, HeaderHome, Card, CardImage, CardButtons, AreaCard, StyledButton1, StyledButton2 } from "./Style";
// import { MdCatchingPokemon } from 'react-icons/md'
// import { ImInfo } from 'react-icons/im'
import { useHistory } from "react-router-dom";
import { useState } from "react"; 
import GlobalState from "../../Context/GlobalState/GlobalState";
import GlobalContext from "../../Context/GlobalContext/GlobalContext";

const HomePage = () => {
  const history = useHistory()
  const detaisPokemons = useContext(GlobalState)
  let [pokedex, setPokedex] = useState([])
  console.log("detais",detaisPokemons)
 
   
    const resultPoke = detaisPokemons && detaisPokemons.map((res) => {
      console.log(res.sprites.front_default)
      return (
        <Card>
          <CardImage>
            <img src={res.sprites.front_default} alt={res.name}/>
            <p>{res.name}</p>
          </CardImage>
          <CardButtons>
            <StyledButton2 > </StyledButton2>
            <StyledButton1 onClick={()=> goToDetails(history,res.name) } ></StyledButton1>
          </CardButtons>
        </Card>)
    })
 


  return (
    <MainContainer >
      <HeaderHome>
        Lista de Pokemons
        <button onClick={() => goToPokedex(history)}>Pokedex</button>
      </HeaderHome>
      <AreaCard>
    {resultPoke}
      </AreaCard>
    </MainContainer>
  );
}

export default HomePage;
