import styled from 'styled-components'

export const PokemonList = styled.div`
display:flex;
flex-direction: row;
flex-wrap: wrap;
padding:50px 4%;
justify-content: center;

`
export const DivPaginacao = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 50px;
  @media (max-width: 800px) {
    margin-top: 4rem;
  }
`;

export const MainDiv = styled.div``;
