import { React, useEffect, useContext } from "react";
import useRequestData from "../../Hooks/useRequestData";
import PokemonCard from "../../Components/PokemonCard/PokemonCard";
import PokemonChoosed from "../../Components/PokemonChoosed/PokemonChoosed";
import { ListaPoke } from "./Style";
import GlobalStateContext from "../../contexts/GlobalContextState";
import { addPokedex } from "../../Services/addPokemon";
import { removePokedex } from "../../Services/removePokemon";

const HomePage = () => {
  const [pokemons] = useRequestData(`?offset=0&limit=30`);
  const { pokedex } = useContext(GlobalStateContext);

  useEffect(() => {}, [pokedex]);

  const pokemonsList =
    pokedex &&
    pokemons &&
    pokemons.results.map((pokemon) => {
      let pokemonExists = pokedex.find((p) => p.name === pokemon.name);
      if (pokemonExists) {
        return (
          <PokemonChoosed
            key={pokemon.name}
            name={pokemon.name}
            pokemon={pokemon}
            removerPokedex={removePokedex}
          />
        );
      } else {
        return (
          <PokemonCard
            key={pokemon.name}
            name={pokemon.name}
            pokemon={pokemon}
            adicionarPokedex={addPokedex}
          />
        );
      }
    });

  return (
    <div>
      <ListaPoke>{pokemonsList}</ListaPoke>
    </div>
  );
};

export default HomePage;
